/**
 * Login handler
 * @param captchaObj
 */
var loginHandler = function (captchaObj) {
    captchaObj.onSuccess(function () {
        var result = captchaObj.getValidate();
        if (!result) {
            $("#notice2").show();
            setTimeout(function () {
                $("#notice2").hide();
            }, 2000);
        } else {
            $('#log_geetest_challenge').val(result.geetest_challenge);
            $('#log_geetest_validate').val(result.geetest_validate);
            $('#log_geetest_seccode').val(result.geetest_seccode);

            $('#auth_email').val(login_first);
            $('#auth_pass').val(pass_first);
            console.log("login " + login_first);
            console.log("pass " + pass_first);
            $('#login_form').submit();

            setTimeout(function () {
                $('#login_form')[0].reset();
                $("#login_submit").prop('disabled', true);
                $('.geetest_holder').remove();
                $("#wait1").css('display', 'block');
                $("#captcha_mssg").css('display', 'none');
            }, 2000);
        }
    })

    captchaObj.appendTo("#captcha1");
    captchaObj.onReady(function () {
        $("#wait1").css('display', 'none');
        $("#captcha_mssg").css('display', 'block');
    });
}

/**
 * Register handler
 * @param captchaObj
 */
var createUserHandler = function (captchaObj) {
    captchaObj.onSuccess(function () {
        var result = captchaObj.getValidate();
        if (!result) {
            $("#notice2").show();
            setTimeout(function () {
                $("#notice2").hide();
            }, 2000);
        } else {
            $.ajax({
                type: "POST",
                url: "/createUser",
                data: {
                    _csrf: $('.s_csrf').val(),
                    email: $('#email').val(),
                    parentEmail: $('#userParentEmail').val(),
                    source: getViewSource(),
                    geetest_challenge: result.geetest_challenge,
                    geetest_validate: result.geetest_validate,
                    geetest_seccode: result.geetest_seccode
                },
                success: function(data) {
                    $.fancybox.close();
                    $('#confirm_email').html(data.user.email);
                    $('#confirm-success').trigger('click');
                }
            });

            setTimeout(function () {
                $('#create_me')[0].reset();
                $("#reg_submit").prop('disabled', true);
                $('.geetest_holder').remove();
                $("#wait1").css('display', 'block');
                $("#captcha_mssg").css('display', 'none');
            }, 2000);

        }
    })

    captchaObj.appendTo("#captcha1");
    captchaObj.onReady(function () {
        $("#wait1").css('display', 'none');
        $("#captcha_mssg").css('display', 'block');
    });
}

